const express = require("express");
const { userInfo } = require("os");
const app = express();
const port = 2000;
const path = require("path");

app.set("view engine", "ejs");
app.set("views", path.join(__dirname, "views"));

app.use(express.urlencoded({extended: true})); 
var methodOverride = require('method-override')
app.use(methodOverride('_method'))


let datas = [
    {
        id: "@pawankumawat",
        image:"data:image/jpeg;base64,",
        caption: "practice base",
    },
    {
        id: "@sdk",
        image:"data:image/jpeg;base64,",
        caption: "practice base",
    },
    {
        id: "@adam",
        image:"data:image/jpeg;base64,",
        caption: "practice base",
    }
]
app.get("/datas", (req, res) =>{
    res.render("index.ejs", { datas });
});

app.get("/datas/add", (req, res) =>{
    res.render("add.ejs")
});
app.post("/datas", (req, res) => {
    let {id, image, caption} = req.body;
    datas.push({id, image, caption});
    res.redirect("/datas")
})

app.get("/datas/:id", (req, res) =>{
    let {id} = req.params;
    let data = datas.find((d)  => id === d.id );
    res.render("show.ejs", { data })
})

app.patch("/datas/:id", (req, res) =>{
    let {id} = req.params;
    let data = datas.find((d) => id === d.id);
    let cap = req.body.caption;
    data.caption = cap;
    res.redirect("/datas");
})

app.get("/datas/:id/edit", (req, res) =>{
    let {id} = req.params;
    let data = datas.find((d) => id === d.id);
    res.render("edit.ejs", {data})
})

app.delete("/datas/:id", (req, res) =>{
    let {id} = req.params;
    datas = datas.filter((d) => id != d.id);
    res.redirect("/datas");
})

app.listen(port, () =>{
    console.log("server is on")
})